import java.util.HashMap;
import java.util.LinkedList;
import java.util.List;

/**********************************************************************************
 *
 * You are given a string, s, and a list of words, words, that are all of the same length. Find all starting indices of substring(s) in s that is a concatenation of each word in words exactly once and without any intervening characters.
 *
 * For example, given:
 * s: "barfoothefoobarman"
 * words: ["foo", "bar"]
 *
 * You should return the indices: [0,9].
 * (order does not matter).
 *
 **********************************************************************************/

public class SubstringWithConcatenationOfAllWords {
    public List<Integer> findSubstring0(String s, String[] words) {
        LinkedList<Integer> list = new LinkedList<Integer>();

        if(words == null || words.length == 0 || words.length*words[0].length() > s.length()) {
            return list;
        }

        int wordLen = words[0].length();
        int wordNum = words.length;

        HashMap<String, Integer> wordsMap = new HashMap<String, Integer>(wordNum);
        for(String word : words) {
            wordsMap.put(word, wordsMap.containsKey(word)?wordsMap.get(word)+1:1);
        }

        for(int i = 0; i < (s.length()-wordLen*wordNum+1); i++) {
            HashMap<String, Integer> curMap = new HashMap<String, Integer>(wordNum);
            int j = 0;
            for(; j < wordNum; j++) {
                String curWord = s.substring(i+wordLen*j, i+wordLen*(j+1));
                if(!wordsMap.containsKey(curWord)) {
                    break;
                } else {
                    if(!curMap.containsKey(curWord)) {
                        curMap.put(curWord, 1);
                    } else {
                        if(wordsMap.get(curWord) <= curMap.get(curWord)) {
                            break;
                        } else {
                            curMap.put(curWord, curMap.get(curWord) + 1);
                        }
                    }
                }

            }
            if(j == wordNum) {
                list.add(i);
            }
        }

        return list;
    }

    public List<Integer> findSubstring(String s, String[] words) {
        LinkedList<Integer> list = new LinkedList<Integer>();

        if(words == null || words.length == 0 || words.length*words[0].length() > s.length()) {
            return list;
        }

        int wordLen = words[0].length();
        int wordNum = words.length;

        HashMap<String, Integer> wordsMap = new HashMap<String, Integer>(wordNum);
        for(String word : words) {
            wordsMap.put(word, wordsMap.containsKey(word)?wordsMap.get(word)+1:1);
        }

        HashMap<String, Integer> curMap = new HashMap<String, Integer>(wordNum);

        for(int i = 0; i < wordLen; i++) {
            curMap.clear();
            int pos = i, j = 0;
            for(; i+wordLen*(j+1) <= s.length(); j++) {
                String curWord = s.substring(i+wordLen*j, i+wordLen*(j+1));
                if(!wordsMap.containsKey(curWord)) {
                    curMap.clear();
                    pos = i + wordLen*(j+1);
                    continue;
                }

                curMap.put(curWord, curMap.containsKey(curWord)?curMap.get(curWord)+1:1);

                while(curMap.get(curWord) > wordsMap.get(curWord)) {
                    String headWord = s.substring(pos, pos + wordLen);
                    if(curMap.get(headWord) == 1) {
                        curMap.remove(headWord);
                    } else {
                        curMap.put(headWord, curMap.get(headWord) - 1);
                    }
                    pos += wordLen;
                }

                if(curMap.equals(wordsMap)) {
                    list.add(pos);
                }
            }
        }
        return list;
    }

    public static void main(String[] args) {
        SubstringWithConcatenationOfAllWords o = new SubstringWithConcatenationOfAllWords();
        System.out.println(o.findSubstring("barfoothefoobarman", new String[]{"foo", "bar"}));
        System.out.println(o.findSubstring(
                ""
                , new String[]{ "ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"}));
    }
}
